import React, { Component } from 'react';
import { Form } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';

class Login extends Component {

    constructor(props){
        super(props);
        this.state = {
            username: '',
            password: '',
            passwordConfirmation: '',
            mode: 'Log In'
        };

        // Bindings
        this.handleButtonClick = this.handleButtonClick.bind(this);
        this.handleChangeUsername = this.handleChangeUsername.bind(this);
        this.handleChangePassword = this.handleChangePassword.bind(this);
        this.handleChangePasswordConf = this.handleChangePasswordConf.bind(this);
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        if (prevState !== this.state){
            console.log(this.state);
        }
    }

    handleButtonClick(){
        let value = 'Log In'
        if (this.state.mode === value){
            value = 'Register';
        }
        this.setState({
            mode: value
        });
    }

    handleChangeUsername(e){
        this.setState({
            username: e.target.value
        });
    }

    handleChangePassword(e){
        this.setState({
            password: e.target.value
        });
    }

    handleChangePasswordConf(e){
        this.setState({
            passwordConfirmation: e.target.value
        });
    }

    render() {
        return (
            <div className='Layout'>
                <header className='Header'>
                    Online Stratego
            </header>

                <Form className='Form' style={{backgroundColor: 'rgba(235,200,70,0.9)' }}>
                    <Form.Group>
                        <Form.Label>Username</Form.Label>
                        <Form.Control className='input' type='text' id='username' onChange={this.handleChangeUsername} />
                    </Form.Group>

                    <Form.Group>
                        <Form.Label>Password</Form.Label>
                        <Form.Control className='input' type='password' id='password' onChange={this.handleChangePassword} />
                    </Form.Group>

                        <Button className='btn btn-warning' onClick={this.handleButtonClick} >
                            Login
                        </Button>
                        <Button className='Button' onClick={this.handleButtonClick} >
                            Register new account
                        </Button>
                </Form>
            </div >

        )
    }
}
export default Login;
