import React, { Component } from 'react';
import {Form} from 'react-bootstrap';
import Button from 'react-bootstrap/Button';

class Menu extends Component {

    render() {
        return (
            < div className='Layout' >
            <header style={{paddingBottom: "1.5rem", fontSize: "55px"}}>
                Online Stratego
            </header>
        
            <Form>
                    <Form.Group>
                        <Form.Label>Gebruikersnaam</Form.Label>
                        <Form.Control className="input" type="text" id="username" onChange={this.setUsername}/>
                    </Form.Group>

                    <Form.Group>

                        <Form.Label>Wachtwoord</Form.Label>
                        <Form.Control className="input" type="password" onChange={this.setPassword}/>
                    </Form.Group>

                    <div className="btnLogin">
                        <Button onClick={this.getUser} className="btn btn-default"
                                style={{background: "transparent", border: "none"}}>
                            <div className="txtLogin">
                                Inloggen
                            </div>
                        </Button>
                    </div>
                </Form>
            </div >

        )}
}
export default Menu;
