import React, { Component } from 'react';
import { Form } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';

class Login extends Component {

    constructor(props){
        super(props);
        this.state = {
            username: '',
            password: '',
            passwordConfirmation: ''
        };
    }

    handleButtonClick(){

    }

    handleChangeUsername(e){
        const value = e.target.value;
        this.setState({
            username: value
        });
    }

    handleChangePassword(e){
        const value = e.target.value;
        console.log(value)
        this.setState({
            password: value
        });
    }

    handleChangePasswordConf(e){
        const value = e.target.value;
        this.setState({
            passwordConfirmation: value
        });
    }

    render() {
        return (
            <div className='Layout'>
                <header className='Header'>
                    Online Stratego
            </header>

                <Form className='Form' style={{ 'background-color': 'rgba(235,200,70,0.9)' }}>
                    <Form.Group>
                        <Form.Label>Username</Form.Label>
                        <Form.Control className='input' type='text' id='username' onChange={this.handleChangeUsername} />
                    </Form.Group>

                    <Form.Group>
                        <Form.Label>Password</Form.Label>
                        <Form.Control className='input' type='password' id='password' onChange={this.handleChangePassword} />
                    </Form.Group>
                    <Form.Group>
                        <Form.Control className='input' type='password' id='passwordConfirmation' onChange={this.handleChangePasswordConf}/>
                    </Form.Group>

                    <div className="btnLogin">
                        <Button onClick={this.handleButtonClick} className="btn btn-default">
                            <div className="txtLogin">
                                Log In
                            </div>
                        </Button>
                    </div>
                </Form>
            </div >

        )
    }
}
export default Login;
