import React, { Component } from 'react';

const height = 800, width = 1200;


class Game extends Component {
    constructor(props) {
        super(props);
        this.state = {
            board: [
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'LAKE', 'LAKE', 'OPEN', 'OPEN', 'LAKE', 'LAKE', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'LAKE', 'LAKE', 'OPEN', 'OPEN', 'LAKE', 'LAKE', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
            ]
        }
    }

    componentDidMount() {
        this.drawBoard();
    }


    drawBoard() {
        console.log('drawing board');
        const canvas = document.getElementById('canvasBoard');
        const context = canvas.getContext('2d');
        context.fillStyle = 'green';

        const board = this.state.board;
        const size = board.length;

        for (var y = 0; y < size; y++) {
            const field = board[y];
            for (var x = 0; x < size; x++) {
                if (field[x] === 'OPEN') {
                    console.log("[" + x + "][" + y + "] " + field[y]);
                    context.fillRect((width / size) * x, (height / size) * y, width / size - 1, height / size - 1);
                }
            }
        }
    }

    render() {
        return (
            <div class="row">
                <div class="col-xs-2 col-sm-2 border">content left</div>
                <div class="col-xs-6 col-sm-6 border" id="main-content">
                    <div class="row">
                        <div class="col-xs-6">
                            Width <input id="w-input" type="number" class="form-control" />
                        </div>
                        <div class="col-xs-6">
                            Height <input id="h-input" type="number" class="form-control" />
                        </div>
                        <div class="col-xs-12 text-right">
                            <button id="set-size" class="btn btn-primary">Set</button>
                        </div>
                    </div>
                    canvas
    <canvas id="canvas"></canvas>

                </div>
                <div class="col-xs-2 col-sm-2 border">content right</div>
            </div>
        )
    }
}

export default Game;
