import React, { Component } from 'react';
import SockJSClient from 'react-stomp';

class Menu extends Component {

    constructor(props) {
        super(props);

        this.createLobby = this.createLobby.bind(this);
        this.sendMessage = this.sendMessage.bind(this);
    }

    createLobby(){
        console.log('clicked')
    }

    onMessageRecieved(message) {
        console.log(message, 'MESSAGE RECIEVED');
    }

    sendMessage = (message) => {
        //this.clientRef.sendMessage('/app/connect', JSON.stringify(message));
        console.log(JSON.stringify(message), 'MESSAGE SEND');
    }
    
    render() {
        return (
            < div className='Layout' >
                <button onClick={this.createLobby}>
                    Create Lobby!
                </button>
                <button onClick={this.joinLobby}>
                    Join Lobby!
                </button>
            </div >
        )
    }
}
export default Menu;
