import React, { Component } from 'react';
import SockJSClient from 'react-stomp';
import ReactCursorPosition , {PositionLabel} from 'react-cursor-position';

const height = 800, width = 1000;

class Game extends Component {
    constructor(props) {
        super(props);
        this.state = {
            ready: false,
            board: [
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'LAKE', 'LAKE', 'OPEN', 'OPEN', 'LAKE', 'LAKE', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'LAKE', 'LAKE', 'OPEN', 'OPEN', 'LAKE', 'LAKE', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
            ]
        }

        // Bindings
        this.sendMessage = this.sendMessage.bind(this);
        this.onMessageRecieved = this.onMessageRecieved.bind(this);
        this.toggle = this.toggle.bind(this);
        this.handleOnClick = this.handleOnClick.bind(this);
        this.getCursorPosition = this.getCursorPosition.bind(this);
    }

    componentDidMount() {
        this.drawBoard();
        const canvas = document.getElementById('canvasPawns');
    }


    drawBoard() {
        console.log('drawing board');
        const canvas = document.getElementById('canvasBoard');
        const context = canvas.getContext('2d');

        const board = this.state.board;
        const size = board.length;

        for (var y = 0; y < size; y++) {
            const field = board[y];
            for (var x = 0; x < size; x++) {
                if (field[x] === 'OPEN') {
                    context.fillStyle = 'green';
                    context.fillRect((width / size) * x, (height / size) * y, width / size - 1, height / size - 1);
                } else if (field[x] === 'LAKE') {
                    context.fillStyle = 'blue';
                    context.fillRect((width / size) * x, (height / size) * y, width / size - 1, height / size - 1);
                }
            }
        }
    }

    handleOnClick(e) {
        e.persist();
        console.log(e);
    }

    onMessageRecieved(message) {
        console.log(message, 'MESSAGE RECIEVED');
    }

    sendMessage = (message) => {
        this.clientRef.sendMessage('/app/game', JSON.stringify(message));
        console.log(JSON.stringify(message), 'MESSAGE SEND');
    }

    toggle = () => {
        this.setState({
            ready: !this.state.ready
        });
        this.sendMessage(this.state);
    }

    render() {
        return (
            <div className='Layout'>
                <header style={{ paddingBottom: '3rem', fontSize: '40px' }}>
                    Online Stratego
                </header>

                <div className='row' id='left'>

                </div>
                <div className='row' id='center'>
                    <div className='grid'>
                        <div className='canvasWrapper'>
                            <ReactCursorPosition>
                                <canvas id='canvasBoard' width={width} height={height} />
                                <PositionLabel/>
                            </ReactCursorPosition>
                        </div>
                        <div className='canvasWrapper'>
                            <canvas id='canvasPawns' width={width} height={height} />
                        </div>
                    </div>
                </div>
                <div className='row' id='right'>

                </div>


                // Websockets
                <button onClick={this.toggle}>
                    toggle
                </button>
                <SockJSClient
                    url={'http://localhost:9000/ws'}
                    topics={['/topic/public']}
                    onMessage={(message) => { this.onMessageRecieved(message); }}
                    ref={(client) => { this.clientRef = client }}
                />
            </div >
        )
    }
}

export default Game;
