import React, { Component } from 'react';

const height = 600, width = 1200;


class Game extends Component {
    constructor(props) {
        super(props);
        this.state = {
            board: [
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'LAKE', 'LAKE', 'OPEN', 'OPEN', 'LAKE', 'LAKE', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'LAKE', 'LAKE', 'OPEN', 'OPEN', 'LAKE', 'LAKE', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
                ['OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN', 'OPEN'],
            ]
        }
    }


    drawBoard() {
        console.log('drawing board');
        const canvas = document.getElementById('canvasBoard');
        const context = canvas.getContext('2d');
        context.fillStyle = 'green';

        const board = this.state.board;
        const size = board.length;

        for (let y = 0; y < size; y++) {
            const field = board[y];
            for (let x = 0; x < size; x++) {
                if (field[x] === 'OPEN'){
                    context.fillRect((this.width / size) * x, (this.heigth / size) * y, this.width / size - 1, this.heigth / size - 1);
                }
            }
        }
    }

    render() {
        return (
            <div className='Layout'>
                <header style={{ paddingBottom: '3rem', fontSize: '40px' }}>
                    Online Stratego
                </header>

                <div className='row'>
                    <div className='grid'>
                        <div className='canvasWrapper'>
                            <canvas id='canvasBoard' width={width} height={height} />
                        </div>
                        <div className='canvasWrapper'>
                            <canvas id='canvasPawns' width={width} height={height} />
                        </div>
                    </div>
                </div>
            </div >
        )
    }
}

export default Game;
